const Reducer = (state, action) => {
    switch(action.type){
        //Login phase
        case "LOGIN_START":
            return{
                user: null,
                isFetching: true,
                error: false
            };
        case "LOGIN_SUCCESS":
            return{
                user: action.payload,
                isFetching: false,
                error: false
            };
        case "LOGIN_FAILURE":
            return{
                user: null,
                isFetching: false,
                error: true
            };
        //Logout phase
        case "LOGOUT":
            return{
                user: null,
                isFetching: false,
                error: false
            };
        //Update phase
        case "UPDATE_START":
            return{
                ...state,
                isFetching: true 
            };
        case "UPDATE_SUCCESS":
            return{
                user: action.payload,
                isFetching: false,
                error: false
            };
        case "UPDATE_FAILURE":
            return{
                user: state.user,
                isFetching: false,
                error: true
            };
        default:
            return state;
    }
}

export default Reducer;
